void Gui::_draw_title(void){
    (*_lcd).SetBackColor(LCD_COLOR_ENSEA);
    (*_lcd).SetTextColor(LCD_COLOR_WHITE);
    BSP_LCD_SetFont(&Font16);
    (*_lcd).DisplayStringAt(0, LINE(2), (uint8_t *)"Commande de", RIGHT_MODE);
    (*_lcd).DisplayStringAt(0, LINE(3), (uint8_t *)"decarotteuse", RIGHT_MODE);
    BSP_LCD_SetFont(&Font20);
    (*_lcd).DisplayStringAt(0, LINE(0), (uint8_t *)"ENSEA", RIGHT_MODE);
    BSP_LCD_SetFont(&Font16);
}

int Gui::display_ts_state(uint8_t ts_init){
    if (ts_init == TS_OK){
      (*_lcd).SetBackColor(LCD_COLOR_BLACK);
      (*_lcd).SetTextColor(LCD_COLOR_GREEN);
      (*_lcd).DisplayStringAt(0, LINE(15), (uint8_t *)"TS INIT OK", LEFT_MODE);
      return 0;
    }
    else{
      (*_lcd).SetBackColor(LCD_COLOR_BLACK);
      (*_lcd).SetTextColor(LCD_COLOR_RED);
      (*_lcd).DisplayStringAt(0, LINE(15), (uint8_t *)"TS INIT FAIL", LEFT_MODE);
      return 1;
    }
}

void Gui::display_cycle(uint8_t cycle){
    sprintf((char*)_text, "cycle = %d", cycle);
    (*_lcd).SetBackColor(LCD_COLOR_ENSEA);
    (*_lcd).SetTextColor(LCD_COLOR_WHITE);
    (*_lcd).DisplayStringAt(0, LINE(16), (uint8_t*) &_text, LEFT_MODE);
}

void Gui::display_current(float curr, float curr_p){
    sprintf((char*)_text, "I= %.1f A -> %.1f %", curr, curr_p);
    (*_lcd).SetBackColor(LCD_COLOR_ENSEA);
    (*_lcd).SetTextColor(LCD_COLOR_WHITE);
    (*_lcd).DisplayStringAt(0, LINE(17), (uint8_t*) &_text, LEFT_MODE);   
}

void Gui::display_x_y(uint16_t xs, uint16_t ys){
    // Display the (x, y)
    sprintf((char*)_text, "x=%d y=%d    ", xs, ys);
    (*_lcd).SetBackColor(LCD_COLOR_ENSEA);
    (*_lcd).SetTextColor(LCD_COLOR_WHITE);
    (*_lcd).DisplayStringAt(0, LINE(18), (uint8_t*) &_text, LEFT_MODE);
}

 void Gui::display_state(STATES state){
    switch (state){
        case UNKNOWN:
            sprintf((char*)_text, "UNKNOWN   ");
            break;
        case IDLE:
            sprintf((char*)_text, "IDLE      ");
            break;
        case MOVE_LEFT:
            sprintf((char*)_text, "MOVE_LEFT ");
            break;
        case MOVE_RIGHT:
            sprintf((char*)_text, "MOVE_RIGHT");
            break;
        case END:
            sprintf((char*)_text, "END       ");
            break;
        default:
            sprintf((char*)_text, "UNKNOWN   ");
    }
    (*_lcd).SetBackColor(LCD_COLOR_ENSEA);
    (*_lcd).SetTextColor(LCD_COLOR_WHITE);
    (*_lcd).DisplayStringAt(0, LINE(19), (uint8_t*) &_text, LEFT_MODE);   
}

/******************************************************************************/

// Constructor
GuiButton::GuiButton(LCD_DISCO_F429ZI *lcdp, int xp, int yp, int wp, int hp){
    _lcd = lcdp;
    _x = xp;
    _y = yp;
    _w = wp;
    _h = hp;
    
    state = 0;
    
    this->display();
}

void GuiButton::display(){
    BSP_LCD_SetFont(&Font16);
    (*_lcd).SetTextColor(LCD_COLOR_BLACK);
    (*_lcd).FillRect(_x-_w/2, _y-_h/2, _w, _h);
    
    if (state){
        (*_lcd).SetBackColor(LCD_COLOR_BLACK);
        (*_lcd).SetTextColor(LCD_COLOR_GREEN);
        (*_lcd).DisplayStringAt(0, LINE(6), (uint8_t *)" ON  ", CENTER_MODE);
    }
    else {
        (*_lcd).SetBackColor(LCD_COLOR_BLACK);
        (*_lcd).SetTextColor(LCD_COLOR_RED);
        (*_lcd).DisplayStringAt(0, LINE(6), (uint8_t *)" OFF ", CENTER_MODE);  
    }
}

void GuiButton::isClicked(int x_cur, int y_cur){
    if ((x_cur < _x - _w/2) ||  (x_cur > _x + _w/2))
        return;
    if ((_y - _h/2 < y_cur ) &&  (y_cur < _y + _h/2)) // is touched
        state = 1 - state; // toggle state
}